public class CCsvDataManager{    public static CCsvDataManager Instance = new CCsvDataManager();    public CServerABVerDataMgr SerABVerDataMgr;    public CLocalABVerDataMgr LocalABVerDataMgr;    public CShopItemDataMgr ShopItemDataMgr;    public CGameDataMgr GameDataMgr;    public CTipsDataMgr TipsDataMgr;    public CVipDataMgr VipDataMgr;    public CGameUIDataMgr GameUIDataMgr;    public CCsvDataManager()    {        SerABVerDataMgr = new CServerABVerDataMgr();        LocalABVerDataMgr = new CLocalABVerDataMgr();        ShopItemDataMgr = new CShopItemDataMgr();        GameDataMgr = new CGameDataMgr();        TipsDataMgr = new CTipsDataMgr();        VipDataMgr = new CVipDataMgr();        GameUIDataMgr = new CGameUIDataMgr();    }    /// <summary>    /// 加载Csv    /// </summary>    public void LoadAllCsv()    {        ShopItemDataMgr.LoadShopItemDataFile();        GameDataMgr.LoadGameDataFile();        TipsDataMgr.LoadTipsCsvFile();        VipDataMgr.LoadVipCsvFile();        GameUIDataMgr.LoadGameUICsvFile();    }}