using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;using XLua;[Hotfix]public class BossInfo
{    public static BossInfo currentboss;    public string icon_;    public string name_;    public int money_;	// Use this for initialization	void Start () {			}		// Update is called once per frame	void Update () {			}    public void initBossInfo( string icon, string name, int money )    {        icon_ = icon;        name_ = name;        money_ = money;    }    void showBossInfo()    {        GameObject obj = GameObject.FindGameObjectWithTag("MaskButton");        GameObject bg = GetChildNode(obj, "dianjialiebiaobg");         for (int index = 0; index < 5; index++)        {            GameObject xinxi = GetChildNode(bg, "xinxi" + index);            GameObject txk = GetChildNode(xinxi, "touxiangkuang");            GameObject jinbi = GetChildNode(xinxi, "jinbi");            //GameObject tx = GetChildNode(txk, "touxiang");            //Image tximg = tx.GetComponent<Image>();            GameObject name = GetChildNode(txk, "name");            Text nametx = name.GetComponent<Text>();            nametx.text = name_;            GameObject jinbishu = GetChildNode(jinbi, "jinbishu");            Text jinbishutx = jinbishu.GetComponent<Text>();            jinbishutx.text = money_.ToString();        }    }    GameObject GetChildNode(GameObject obj, string name)    {        Transform transform = obj.transform.Find(name);        return transform.gameObject;    }}