using UnityEngine;using UnityEngine.UI;using XLua;[Hotfix]public class Ex_PicFont{    string startStr;    GameObject go;    string textStr;    public void Init(string _startStr,GameObject _go)    {        startStr = _startStr;        go = _go;    }    public void SetText(string _textStr)    {        textStr = _textStr;        int count = textStr.Length;        int ac = go.transform.childCount;        if (ac > count)        {            for (int i = ac; i > count; i--)            {                Object.Destroy(go.transform.GetChild(i-1).gameObject);            }        }        else if (ac < count)        {            for (int i = ac; i < count; i++)            {                GameObject go_temp = new GameObject();                go_temp.transform.SetParent(go.transform);                go_temp.AddComponent<Image>();            }        }        for (int i = 0; i < count; i++)        {            Image img_temp = go.transform.GetChild(i).gameObject.GetComponent<Image>();            string temp_str = textStr.Substring(i, 1);            if (temp_str == "+")            {                temp_str = "jia";            } else if (temp_str == "-")            {                temp_str = "jian";            }            img_temp.sprite = CGame_DiuDiuLe.BundleIns().LoadAsset<Sprite>(startStr + temp_str);            img_temp.SetNativeSize();        }    }    public string GetText()    {        if (textStr != null)        {            return textStr;        }        else        {            return "0";        }    }}