using UnityEngine;using XLua;[Hotfix]public class Ex_Time
{    GameObject timeUI;    Transform trs_daojishi;    float totalTime = 18.0f;    float lastTime;    bool isStart = false;	public void Update () {        if (trs_daojishi != null && isStart)        {            lastTime -= Time.deltaTime;            if (lastTime <= 0)            {                StopTime();            }            else            {                trs_daojishi.gameObject.GetComponent<UnityEngine.UI.Image>().fillAmount = lastTime / totalTime;            }        }    }    public void InitUI(GameObject _timeUI)    {        timeUI = _timeUI;        trs_daojishi = timeUI.transform;    }    public void StartTime()    {        timeUI.SetActive(true);        lastTime = totalTime;        isStart = true;    }    public void StopTime()    {        isStart = false;        timeUI.SetActive(false);    }}