using System.Collections;using System.Collections.Generic;using UnityEngine;using USocket.Messages;public class TP_DataCenter{    static TP_DataCenter instance;    public TexasPokerLogin tpl;    public TexasPokerEnterRoomMSG tper = new TexasPokerEnterRoomMSG();    TP_DataCenter()    {        tper = new TexasPokerEnterRoomMSG();    }    public static TP_DataCenter Instance()    {        if (instance == null)            instance = new TP_DataCenter();        return instance;    }    public void ReadRoomDataFromServer(UMessage msg)    {        tpl = new TexasPokerLogin();        tpl.ReadMessage(msg);    }}