using System.Collections.Generic;/// <summary>/// 公会管理类/// </summary>public class GuildManager{    public static readonly GuildManager Instance = new GuildManager();    //公会map    private Dictionary<uint, Guild>   m_GuildDictionryID;    private Dictionary<string, Guild> m_GuildDictionryName;    private GuildManager()    {        m_GuildDictionryID = new Dictionary<uint, Guild>();        m_GuildDictionryName = new Dictionary<string, Guild>();    }    /// <summary>    /// 通过公会ID获取公会    /// </summary>    /// <param name="guildId"></param>    /// <returns></returns>    public Guild GetGuildById(uint guildId)    {        if (m_GuildDictionryID.ContainsKey(guildId))            return m_GuildDictionryID[guildId];        return null;    }    /// <summary>    /// 通过名称获取公会    /// </summary>    /// <param name="name"></param>    /// <returns></returns>    public Guild GetGuildByName(string name)    {        if (m_GuildDictionryName.ContainsKey(name))            return m_GuildDictionryName[name];        return null;    }    /// <summary>    /// 创建公会    /// </summary>    /// <param name="playerId"></param>    /// <param name="guildname"></param>    /// <returns></returns>    public bool CreateGuild(uint playerId,string guildname)    {        return true;    }    /// <summary>    /// 解散公会    /// </summary>    /// <param name="playerId"></param>    /// <returns></returns>    public bool DisbandGuild(uint playerId)    {        return true;    }}