using System.Collections;using System.Collections.Generic;using UnityEngine;public class MomentsMember{    public MomentsMember()    {        memberid = 0;        memberName = "";        faceid = 0;        url = "";        isonline = false;        masterScore = 0;    }    public uint memberid;    public string memberName;    public int faceid;    public string url;    public bool isonline;    public float masterScore;}public class MomentsSeat{    public MomentsSeat(byte sitNo)    {        seatNo = sitNo;        playerid = 0;    }    public byte seatNo;    public uint playerid;}public class MomentsTable{    public MomentsTable(uint number)    {        seats_ = new List<MomentsSeat>();        playerNumber = number;        for(int index = 0; index < playerNumber; index++)        {            MomentsSeat seat = new MomentsSeat((byte)index);            seats_.Add(seat);        }        llr = new LandLordRule();        ter = new ThrowEggsRule();    }    public uint tableid;    public byte gameid;    public ThrowEggsRule ter;    public LandLordRule llr;    public uint playerNumber;    public List<MomentsSeat> seats_;    public uint hostid;    public string gamerule;}public class FriendsMomentsDataMamager{	FriendsMomentsDataMamager()    {        momentsID = 0;        momentsName = "";        content = "";        friendDiamond = 0;        joinList = new Dictionary<uint, MomentsMember>();        memberList = new Dictionary<uint, global::MomentsMember>();        tableList = new Dictionary<uint, MomentsTable>();    }	    public static FriendsMomentsDataMamager GetFriendsInstance()    {        if (instance_ == null)            instance_ = new FriendsMomentsDataMamager();        return instance_;    }    public MomentsTable GetTableByID(uint tableid)    {        if (tableList.ContainsKey(tableid))            return tableList[tableid];        return null;    }    public uint momentsID;    public uint hostid;    public string momentsName;    public string content;    public long friendDiamond;    public Dictionary<uint, MomentsMember> joinList;    public Dictionary<uint, MomentsMember> memberList;    public Dictionary<uint, MomentsTable> tableList;    public long currentvideoid;    static FriendsMomentsDataMamager instance_;}